import React, { useState, useEffect } from 'react';
import Home from './components/Home';
import Login from './components/Login';
import Signup from './components/Signup';
import AuthModal from './components/AuthModal'; // Modal för inloggning och registrering
import { signInAnonymously, onAuthStateChanged, signOut } from 'firebase/auth';
import { auth } from './firebaseConfig';


const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [currentScreen, setCurrentScreen] = useState(null); // Hanterar vilken modal som visas
  const [showAuthModal, setShowAuthModal] = useState(false); // Hanterar om modalen visas
  const [loading, setLoading] = useState(true); // Lägg till loading state
  const [user, setUser] = useState(null); // För att hålla koll på användarinformation

  useEffect(() => {
    // Kolla om användaren redan är inloggad
    const unsubscribe = onAuthStateChanged(auth, (user) => {
      if (user) {
        setIsAuthenticated(true);
        setUser(user); // Sätt användarinformationen
      } else {
        setIsAuthenticated(false);
        setUser(null); // Ta bort användarinformation om inte inloggad
      }
      setLoading(false); // När authstatusen har laddats, sätt loading till false
    });

    return () => unsubscribe();
  }, []);

  const handleOpenAuthModal = (mode) => {
    setCurrentScreen(mode); // Sätt modalen som login eller register
    setShowAuthModal(true);
  };

  const handleCloseAuthModal = () => {
    setShowAuthModal(false);
  };

  const handleLogout = async () => {
    await signOut(auth); // Logga ut användaren
  };

  const handleLoginSuccess = () => {
    setShowAuthModal(false); // Stäng modalen om användaren loggar in
  };

  return (
    <Router>
      <div>
        {/* Knapp för att öppna inloggnings-/registreringsmodal */}
        <div style={{ position: 'absolute', top: '10px', right: '10px' }}>
          {isAuthenticated ? (
            <>
              <span>Välkommen, {user.displayName || 'Användare'}</span>
              <button onClick={handleLogout}>Logga ut</button>
              {/* Knapp för att visa historik */}
              <Link to="/historik">
                <button>Visa Historik</button>
              </Link>
            </>
          ) : (
            <>
              <button onClick={() => handleOpenAuthModal('login')}>Logga in</button>
              <button onClick={() => handleOpenAuthModal('register')}>Registrera</button>
            </>
          )}
        </div>

        {/* Huvudsida */}
        {loading ? (
          <div>Laddar...</div> // Visa laddning när autentisering är pågående
        ) : (
          <Home />
        )}

        {/* Modal för inloggning eller registrering */}
        {showAuthModal && (
          <AuthModal mode={currentScreen} onClose={handleCloseAuthModal} />
        )}

        {/* Definiera routes */}
        <Switch>
          <Route path="/historik" component={HistoryPage} />
          <Route path="/" render={() => <Home />} />
        </Switch>
      </div>
    </Router>
  );
};

export default App;
