// src/components/HistoryPage.js
import React, { useEffect, useState } from 'react';
import { getDatabase, ref, get } from 'firebase/database'; // Firebase Realtime Database
import { useNavigate } from 'react-router-dom';  // Importera useNavigate för navigering
import './HistoryPage.css';  // Importera CSS-filen

const HistoryPage = () => {
  const [conversations, setConversations] = useState([]);
  const navigate = useNavigate(); // Skapa en instans av navigate

  useEffect(() => {
    const fetchConversations = async () => {
      const db = getDatabase(); // Hämta referens till databasen
      const conversationsRef = ref(db, 'conversations/'); // Referens till 'conversations' i databasen

      try {
        const snapshot = await get(conversationsRef); // Hämta data från Firebase
        if (snapshot.exists()) {
          const data = snapshot.val(); // Data från Firebase
          console.log("Data från Firebase:", data); // Logga datan för att se vad som hämtas

          // Om data finns, konvertera det till en lista med alla konversationer
          const conversationsList = Object.keys(data).map(key => ({
            id: key,
            ...data[key], // Lägg till all information från konversationen
          }));

          setConversations(conversationsList); // Sätt konversationerna i state
        } else {
          console.log("Inga konversationer tillgängliga.");
        }
      } catch (error) {
        console.log("Fel vid hämtning av konversationer:", error);
      }
    };

    fetchConversations();
  }, []); // Endast körs när komponenten laddas

  // Funktion för att navigera tillbaka till huvudsidan
  const handleGoBack = () => {
    navigate('/'); // Navigera till huvudsidan
  };

  return (
    <div className="history-container">
      <h2>Historik</h2>
      <ul>
        {conversations.length > 0 ? (
          conversations.map((conversation) => (
            <li key={conversation.id}>
              <p><strong>Prompt:</strong> {conversation.Prompt}</p>
              <p><strong>Answer:</strong> {conversation.Answer}</p>
              <p><strong>Date:</strong> {conversation.Date}</p>

              {/* Om det finns ljud-URL:er, visa knappar för att spela upp ljudet */}
              {conversation.PromptAudioURL && (
                <div>
                  <button onClick={() => new Audio(conversation.PromptAudioURL).play()}>
                    Spela upp Prompt ljud
                  </button>
                </div>
              )}
              {conversation.AnswerAudioURL && (
                <div>
                  <button onClick={() => new Audio(conversation.AnswerAudioURL).play()}>
                    Spela upp Answer ljud
                  </button>
                </div>
              )}
            </li>
          ))
        ) : (
          <p>Inga konversationer hittades.</p>
        )}
      </ul>

      {/* Knapp för att gå tillbaka till huvudsidan */}
      <button className="back-button" onClick={handleGoBack}>Tillbaka till Hemsidan</button>
    </div>
  );
};

export default HistoryPage;
