import React, { useState, useEffect, useRef } from 'react';
import { uploadAudio } from '../client';
import '../style.css'; // Importera CSS-fil

const AudioUploader = () => {
  const [audioBlob, setAudioBlob] = useState(null);
  const [isRecording, setIsRecording] = useState(false);
  const [isPaused, setIsPaused] = useState(false); // Ny state för att hålla reda på om inspelningen är pausad
  const [mediaRecorder, setMediaRecorder] = useState(null);
  const [responseAudio, setResponseAudio] = useState(null);
  const [loading, setLoading] = useState(false);
  const audioRef = useRef(null);
  const canvasRef = useRef(null); // Ref till canvas för vågformsanimation
  const analyserRef = useRef(null); // Ref till AnalyserNode
  const audioContextRef = useRef(null); // Ref till AudioContext
  const microphoneRef = useRef(null); // För att spara mikrofonströmmen
  const MAX_RECORDING_TIME = 5000;
  let recordingTimeout = null;

  // För att hålla koll på ljudnivåerna och när tystnaden uppstår
  const silenceThreshold = 5; // Antalet på var tystnaden måste vara innan vi stoppar
  const silenceDuration = 1500; // Hur lång tid tystnaden måste vara (ms)
  let silenceTimer = null;

  useEffect(() => {
    const setupRecorder = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
        const options = { mimeType: 'audio/webm' };
        const recorder = new MediaRecorder(stream, options);
        setMediaRecorder(recorder);

        // Skapa en AudioContext och AnalyserNode för att läsa ljuddata
        audioContextRef.current = new (window.AudioContext || window.webkitAudioContext)();
        analyserRef.current = audioContextRef.current.createAnalyser();
        analyserRef.current.fftSize = 256; // För upplösning på ljudet
        microphoneRef.current = audioContextRef.current.createMediaStreamSource(stream);
        microphoneRef.current.connect(analyserRef.current);
      } catch (error) {
        console.error('Fel vid åtkomst till mikrofonen:', error);
      }
    };

    setupRecorder();
  }, []);

  useEffect(() => {
    // Funktion för att rita vågformsanimation
    const drawWaveform = () => {
      if (!canvasRef.current || !isRecording || isPaused) return; // Stoppa animationen om inspelningen är pausad eller inte aktiv

      const canvas = canvasRef.current;
      const ctx = canvas.getContext('2d');
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // Analyserar ljuddata från mikrofonen
      const bufferLength = analyserRef.current.frequencyBinCount;
      const dataArray = new Uint8Array(bufferLength);
      analyserRef.current.getByteFrequencyData(dataArray);

      if (dataArray.every(value => value === 0)) {
        // Om alla värden är noll (tystnad), stoppa animationen
        return;
      }

      const sliceWidth = canvas.width * 1.0 / bufferLength;
      let x = 0;

      // Rita vågformen
      for (let i = 0; i < bufferLength; i++) {
        const v = dataArray[i] / 255;
        const y = v * canvas.height;

        ctx.fillStyle = '#3b82f6';
        ctx.fillRect(x, canvas.height - y, sliceWidth, y);
        x += sliceWidth;
      }

      requestAnimationFrame(drawWaveform); // Uppdatera animationen
    };

    if (isRecording && !isPaused) {
      drawWaveform(); // Starta animation när inspelning börjar och inte är pausad
    }
  }, [isRecording, isPaused]);

  useEffect(() => {
    // Kontrollera ljudnivån kontinuerligt
    const checkSilence = () => {
      const bufferLength = analyserRef.current.frequencyBinCount;
      const dataArray = new Uint8Array(bufferLength);
      analyserRef.current.getByteFrequencyData(dataArray);

      const averageVolume = dataArray.reduce((sum, value) => sum + value, 0) / bufferLength;

      if (averageVolume < silenceThreshold) {
        if (!silenceTimer) {
          silenceTimer = setTimeout(() => {
            handleStopRecording();
          }, silenceDuration);
        }
      } else {
        clearTimeout(silenceTimer);
        silenceTimer = null;
      }
    };

    if (isRecording && !isPaused) {
      const interval = setInterval(checkSilence, 200); // Kolla varje 200 ms
      return () => clearInterval(interval); // Rensa intervall när komponenten tas bort
    }
  }, [isRecording, isPaused]);

  const handleStartRecording = () => {
    if (!mediaRecorder) return;

    if (isPaused) {
      // Återuppta inspelning om den är pausad
      mediaRecorder.resume();
      setIsPaused(false);
    } else {
      // Starta inspelning från början
      mediaRecorder.start();
      setIsRecording(true);
    }

    recordingTimeout = setTimeout(() => {
      handleStopRecording();
    }, MAX_RECORDING_TIME);

    mediaRecorder.ondataavailable = (event) => {
      const audioBlob = new Blob([event.data], { type: 'audio/webm' });
      setAudioBlob(audioBlob);
    };
  };

  const handleStopRecording = () => {
    if (mediaRecorder && mediaRecorder.state === 'recording') {
      mediaRecorder.stop();
      clearTimeout(recordingTimeout);
      setIsRecording(false);
      setIsPaused(false); // Säkerställ att paus är avstängd när inspelningen stoppas
    }
  };

  const handlePauseRecording = () => {
    if (mediaRecorder && mediaRecorder.state === 'recording') {
      mediaRecorder.pause();
      setIsPaused(true);
    }
  };

  const handleUpload = async (blob) => {
    setLoading(true);

    try {
      const audioBlobResponse = await uploadAudio(blob);
      const audioURL = URL.createObjectURL(audioBlobResponse);
      setResponseAudio(audioURL);
    } catch (error) {
      console.error('Fel vid uppladdning:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (responseAudio && audioRef.current) {
      audioRef.current.play();
    }
  }, [responseAudio]);

  return (
    <div className="audio-uploader">
      <h2 className="title">Spela in ett ljud</h2>
      <div className="recording-controls">
        {!isRecording ? (
          <button onClick={handleStartRecording} disabled={loading} className="btn start-btn">
            Starta inspelning
          </button>
        ) : isPaused ? (
          <button onClick={handleStartRecording} disabled={loading} className="btn resume-btn">
            Återuppta inspelning
          </button>
        ) : (
          <>
            <button onClick={handleStopRecording} disabled={loading} className="btn stop-btn">
              Stoppa inspelning
            </button>
            <button onClick={handlePauseRecording} disabled={loading} className="btn pause-btn">
              Pausa inspelning
            </button>
          </>
        )}
        {loading && <p className="loading-text">Bearbetar ljud...</p>}
      </div>

      {/* Vågformsanimation */}
      <canvas
        ref={canvasRef}
        width={300}
        height={100}
        className="waveform-canvas"
      ></canvas>

      {responseAudio && (
        <div className="audio-preview">
          <h3>Bearbetat ljud</h3>
          <audio ref={audioRef} src={responseAudio} controls className="audio-player"></audio>
        </div>
      )}
    </div>
  );
};

export default AudioUploader;
