// src/components/AuthModal.js
import React from 'react';
import Login from './Login';
import Signup from './Signup';

const AuthModal = ({ mode, onClose }) => {
    return (
      <div>
        <h2>{mode === 'login' ? 'Logga in' : 'Registrera'}</h2>
        <form>
          {/* Formulärfält för användarnamn, e-post, lösenord etc. */}
          <input type="email" placeholder="E-post" required />
          <input type="password" placeholder="Lösenord" required />
          <button type="submit">{mode === 'login' ? 'Logga in' : 'Registrera'}</button>
        </form>
        <button onClick={onClose}>Stäng</button>
      </div>
    );
  };

export default AuthModal;
