import React, { useState, useEffect } from 'react';

// Komponent för att visa ljudvågsanimationen
const SoundWaves = ({ volume }) => {
  const numWaves = 5; // Antal ljudvågor

  // Generera en array med olika ljudvågshöjder baserat på volymnivån
  const waveHeights = Array.from({ length: numWaves }, () =>
    Math.random() * volume
  );

  return (
    <div className="sound-waves">
      {waveHeights.map((height, index) => (
        <div
          key={index}
          className="wave"
          style={{ height: `${height}%`, width: `${20 + Math.random() * 10}px` }}
        ></div>
      ))}
    </div>
  );
};

const VoiceInput = () => {
  const [isListening, setIsListening] = useState(false);
  const [volume, setVolume] = useState(0);

  useEffect(() => {
    const SpeechRecognition =
      window.SpeechRecognition || window.webkitSpeechRecognition;

    if (!SpeechRecognition) {
      console.log('Web Speech API stöds inte i denna webbläsare.');
      return;
    }

    const recognition = new SpeechRecognition();
    recognition.continuous = true; // Lyssna kontinuerligt
    recognition.interimResults = true; // Visa mellanresultat under talet

    recognition.onstart = () => {
      setIsListening(true);
    };

    recognition.onend = () => {
      setIsListening(false);
    };

    recognition.onresult = (event) => {
      let volumeLevel = 0;
      for (let i = event.resultIndex; i < event.results.length; i++) {
        if (event.results[i].isFinal) {
          const result = event.results[i][0].transcript;
          console.log('Talad text:', result);
        } else {
          // Beräkna volymnivån baserat på mellanresultat (kan justeras för mer precisa värden)
          volumeLevel = Math.random() * 100; // Simulerar volym baserat på mellanresultat
        }
      }
      setVolume(volumeLevel); // Uppdatera volymnivån
    };

    recognition.start();

    // Rensa upp vid komponentens avmontering
    return () => {
      recognition.stop();
    };
  }, []);

  return (
    <div className="voice-input-container">
      <h3>{isListening ? 'Lyssnar...' : 'Klar för röstinmatning'}</h3>
      <SoundWaves volume={volume} />
    </div>
  );
};

export default VoiceInput;
