export const uploadAudio = async (audioBlob) => {
  const formData = new FormData();
  formData.append('file', audioBlob, 'audio.webm');

  try {
    const response = await fetch('DIN_UPPLADDNINGS_URL', {
      method: 'POST',
      body: formData,
    });

    if (!response.ok) {
      throw new Error('Uppladdning misslyckades');
    }

    const data = await response.blob(); // Eller använd .json() om du förväntar dig JSON-svar
    return data;
  } catch (error) {
    throw new Error(`Fel vid uppladdning: ${error.message}`);
  }
};
