export const uploadAudio = async (blob) => {
  const formData = new FormData();
  formData.append('audio', blob, 'audio.webm'); // Lägg till Blob i FormData

  try {
    const response = await fetch('/api/process-audio', {
      method: 'POST',
      body: formData,
    });

    if (!response.ok) {
      // Om responsen inte är OK, logga status och text för felsökning
      const errorText = await response.text();
      console.error(`Fel vid uppladdning: ${response.status} - ${errorText}`);
      throw new Error(`Fel vid uppladdning: ${response.status} - ${errorText}`);
    }

    return await response.blob(); // Returnera bearbetat ljud som Blob från servern
  } catch (error) {
    console.error('Fel vid uppladdning:', error);
    throw new Error('Fel vid uppladdning');
  }
};
