// Waveform.js
import React, { useEffect, useRef } from 'react';
import WaveSurfer from 'wavesurfer.js';

const Waveform = () => {
  const waveformRef = useRef(null);
  const wavesurferRef = useRef(null);

  useEffect(() => {
    // Initiera wavesurfer för att skapa en vågformsanimation
    wavesurferRef.current = WaveSurfer.create({
      container: waveformRef.current,
      waveColor: '#A0E7E5', // Färg på vågformen
      progressColor: '#FFAEBC', // Färg på vågformens progress
      cursorWidth: 0,
      barWidth: 2,
      barHeight: 1,
      barGap: 2,
      responsive: true,
    });

    // Fånga ljud från mikrofonen
    navigator.mediaDevices.getUserMedia({ audio: true })
      .then((stream) => {
        // Använd mikrofonens ljudströmming som input till wavesurfer
        wavesurferRef.current.loadDecodedBuffer(stream);

        // Starta vågformsanimation när ljud finns
        wavesurferRef.current.on('audioprocess', () => {
          if (wavesurferRef.current.getCurrentTime() > 0) {
            wavesurferRef.current.play();
          }
        });
      })
      .catch((err) => console.error('Fel vid mikrofonanslutning', err));

    return () => {
      if (wavesurferRef.current) {
        wavesurferRef.current.destroy();
      }
    };
  }, []);

  return (
    <div>
      <h2>Vågformsanimation</h2>
      <div ref={waveformRef} style={{ width: '100%', height: '100px' }} />
    </div>
  );
};

export default Waveform;
