import React, { useState, useEffect } from 'react';
import '../style.css'; // Använd CSS för modal

// Du kan anpassa den här komponenten för att visa en mikrofon och dess animation
const VoiceActivityIndicator = () => {
  const [isListening, setIsListening] = useState(false);

  // Simulera att mikrofonen är aktiv under 3 sekunder, för exempel
  useEffect(() => {
    const interval = setInterval(() => {
      setIsListening((prev) => !prev); // Växla mellan aktiv och inaktiv
    }, 1000); // Växla varje sekund
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="voice-activity-indicator">
      <div
        style={{
          width: '50px',
          height: '50px',
          borderRadius: '50%',
          backgroundColor: isListening ? 'green' : 'gray', // Grön när aktiv, grå när inaktiv
          animation: isListening ? 'pulse 1s infinite' : 'none',
        }}
      ></div>
      <p>{isListening ? 'Lyssnar...' : 'Inaktiv'}</p>
    </div>
  );
};

const LanguageSelector = () => {
  return (
    <div className="language-selector">
      <h3>Välj språk eller använd talinmatning</h3>
      <VoiceActivityIndicator /> {/* Lägg till voice activity indicator här */}
    </div>
  );
};

export default LanguageSelector;
