export const uploadAudio = async (audioBlob) => {
    const formData = new FormData();
    formData.append('audio', audioBlob, 'audio.webm'); // Se till att filnamnet st채mmer

    const response = await fetch('http://localhost:3000/api/process-audio', {
      method: 'POST',
      body: formData,
    });

    if (!response.ok) {
      throw new Error('N책got gick fel vid uppladdning av ljudet');
    }

    return await response.blob(); // Returnera ljudfilen som en Blob
  };
