import React, { useState, useEffect } from 'react';

const AudioUploader = () => {
  const [audioBlob, setAudioBlob] = useState(null);
  const [isRecording, setIsRecording] = useState(false);
  const [mediaRecorder, setMediaRecorder] = useState(null);
  const [responseAudio, setResponseAudio] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    // Begär åtkomst till mikrofonen och sätt upp MediaRecorder när komponenten laddas
    const setupRecorder = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
        const recorder = new MediaRecorder(stream);
        setMediaRecorder(recorder);
      } catch (error) {
        console.error('Fel vid åtkomst till mikrofonen:', error);
      }
    };

    setupRecorder();
  }, []);

  const handleStartRecording = () => {
    if (!mediaRecorder) return;

    mediaRecorder.start();
    setIsRecording(true);

    const audioChunks = [];
    mediaRecorder.ondataavailable = (event) => {
      audioChunks.push(event.data);
    };

    mediaRecorder.onstop = () => {
      const audioBlob = new Blob(audioChunks, { type: 'audio/webm' });
      setAudioBlob(audioBlob);
      setIsRecording(false);
    };
  };

  const handleStopRecording = () => {
    if (mediaRecorder && isRecording) {
      mediaRecorder.stop();
    }
  };

  const handleUpload = async () => {
    if (!audioBlob) {
      alert('Vänligen spela in ett ljud först.');
      return;
    }

    const formData = new FormData();
    formData.append('audio', audioBlob, 'recorded_audio.webm');

    setLoading(true);

    try {
      const response = await fetch('http://localhost:3000/api/process-audio', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error('Serverfel vid bearbetning av ljudet.');
      }

      const audioBlob = await response.blob();
      const audioURL = URL.createObjectURL(audioBlob);
      setResponseAudio(audioURL);
    } catch (error) {
      console.error('Fel:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ textAlign: 'center' }}>
      <h2>Spela in och ladda upp ljud</h2>

      {!isRecording ? (
        <button onClick={handleStartRecording} disabled={loading}>
          Starta inspelning
        </button>
      ) : (
        <button onClick={handleStopRecording} disabled={loading}>
          Stoppa inspelning
        </button>
      )}

      <br />
      {audioBlob && <button onClick={handleUpload} disabled={loading}>Ladda upp inspelat ljud</button>}

      {loading && <p>Bearbetar ljud...</p>}

      {responseAudio && (
        <div>
          <h3>Bearbetat ljud</h3>
          <audio controls src={responseAudio}></audio>
        </div>
      )}
    </div>
  );
};

export default AudioUploader;
