// LanguageSelector.js
import React from 'react';
import LanguageCard from './LanguageCard';
// LanguageSelector.js
import './LanguageSelector.css'; // Lägg till denna rad


const languages = [
  { name: 'Svenska', flag: 'path/to/swedish-flag.png' },
  { name: 'Engelska', flag: 'path/to/english-flag.png' },
  { name: 'Spanska', flag: 'path/to/spanish-flag.png' },
  // Lägg till fler språk här
];

const LanguageSelector = ({ onSelectLanguage }) => {
  return (
    <div className="language-selector">
      <h2>Välj ett språk att lära dig:</h2>
      <div className="language-cards">
        {languages.map((lang, index) => (
          <LanguageCard
            key={index}
            language={lang.name}
            flag={lang.flag}
            onSelect={onSelectLanguage}
          />
        ))}
      </div>
    </div>
  );
};

export default LanguageSelector;
