// LanguageCard.js
import React from 'react';

const LanguageCard = ({ language, flag, onSelect }) => {
  return (
    <div className="language-card" onClick={() => onSelect(language)}>
      <img src={flag} alt={`${language} flag`} />
      <h3>{language}</h3>
    </div>
  );
};

export default LanguageCard;
