import React, { useState, useEffect } from 'react';
import { uploadAudio } from '../client'; // Importera din nya client.js

const AudioUploader = () => {
  const [audioBlob, setAudioBlob] = useState(null);
  const [isRecording, setIsRecording] = useState(false);
  const [mediaRecorder, setMediaRecorder] = useState(null);
  const [responseAudio, setResponseAudio] = useState(null);
  const [loading, setLoading] = useState(false);
  const [transcription, setTranscription] = useState(""); // Ny state för transkription

  useEffect(() => {
    const setupRecorder = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
        const recorder = new MediaRecorder(stream, { mimeType: 'audio/wav' }); // Använder audio/wav format
        setMediaRecorder(recorder);
      } catch (error) {
        console.error('Fel vid åtkomst till mikrofonen:', error);
      }
    };

    setupRecorder();
  }, []);

  const handleStartRecording = () => {
    if (!mediaRecorder) return;

    mediaRecorder.start();
    setIsRecording(true);
    setTranscription("Spelar in..."); // Simulerad transkription

    const audioChunks = [];
    mediaRecorder.ondataavailable = (event) => {
      audioChunks.push(event.data);
    };

    mediaRecorder.onstop = () => {
      const audioBlob = new Blob(audioChunks, { type: 'audio/wav' }); // Spara ljudet som .wav
      setAudioBlob(audioBlob);
      setIsRecording(false);
      setTranscription(""); // Rensa transkriptionen när inspelningen stoppas
    };
  };

  const handleStopRecording = () => {
    if (mediaRecorder && isRecording) {
      mediaRecorder.stop();
      setTranscription("Inspelning stoppad. Bearbetar..."); // Simulerad transkription
    }
  };

  const handleUpload = async () => {
    if (!audioBlob) {
      alert('Vänligen spela in ett ljud först.');
      return;
    }

    setLoading(true);

    try {
      const audioBlobResponse = await uploadAudio(audioBlob); // Anropa backend via client.js
      const audioURL = URL.createObjectURL(audioBlobResponse);
      setResponseAudio(audioURL);
      setTranscription("Transkription: (simulerad text här)"); // Simulerad transkription
    } catch (error) {
      console.error('Fel vid uppladdning:', error);
      setTranscription("Fel vid uppladdning."); // Visa felmeddelande
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ textAlign: 'center' }}>
      <h2>Spela in och ladda upp ljud</h2>

      {!isRecording ? (
        <button onClick={handleStartRecording} disabled={loading}>
          Starta inspelning
        </button>
      ) : (
        <button onClick={handleStopRecording} disabled={loading}>
          Stoppa inspelning
        </button>
      )}

      <br />
      {audioBlob && <button onClick={handleUpload} disabled={loading}>Ladda upp inspelat ljud</button>}

      {loading && <p>Bearbetar ljud...</p>}
      {transcription && <p>{transcription}</p>} {/* Visa transkriptionen */}

      {responseAudio && (
        <div>
          <h3>Bearbetat ljud</h3>
          <audio controls src={responseAudio}></audio>
        </div>
      )}
    </div>
  );
};

export default AudioUploader;
