import React, { useEffect, useState } from 'react';

const LanguageSelector = () => {
    const [languages, setLanguages] = useState([]);

    useEffect(() => {
        const fetchLanguages = async () => {
            try {
                const response = await fetch('https://restcountries.com/v3.1/all');
                const data = await response.json();
                // Filter to include only languages you want
                const selectedLanguages = data.map(country => ({
                    name: country.name.common,
                    flag: country.flags.svg, // Using the SVG version of the flag
                })).filter(language => ['Sweden', 'Spain', 'United Kingdom'].includes(language.name));

                setLanguages(selectedLanguages);
            } catch (error) {
                console.error('Error fetching languages:', error);
            }
        };

        fetchLanguages();
    }, []);

    return (
        <div>
            {languages.map((language) => (
                <div key={language.name} className="language-card">
                    <img src={language.flag} alt={`${language.name} flag`} />
                    <h3>{language.name}</h3>
                </div>
            ))}
        </div>
    );
};

export default LanguageSelector;
